import React, { useState } from "react";
import "../../App.css";
import "./CustomDictionary.css";
import Cards from "../Cards";
import HeroSection from "../HeroSection";
import Footer from "../Footer";
import ProjectsJSON from "./Projects.json";

function CustomDictionary() {
  const [inputTitle, setInputTitle] = useState("");
  return (
    <div className="custom-dictionary">
      {ProjectsJSON.filter((val) => {
        if (inputTitle == "" && inputTags == "" && inputDescription == "") {
          return val;
        } else if (
          val.title.toLowerCase().includes(inputTitle.toLowerCase()) &&
          val.tags.toLowerCase().includes(inputTags.toLowerCase()) &&
          val.description.toLowerCase().includes(inputDescription.toLowerCase())
        ) {
          return val;
        }
      }).map((val, key) => {
        return (
          <div
            style={{
              borderRadius: 10,
              cursor: "pointer",
              backgroundColor: "white",
              margin: 20,
              padding: 20,
            }}
            onClick={() => {
              window.location.href = val.url;
            }}
          >
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                justifyContent: "center",
              }}
            >
              <div style={{ width: 1000 }}>
                <strong>{val.title}</strong>
                <p>{val.description}</p>
                <p>tags: {val.tags}</p>
              </div>
              {/* <div style={{justifyContent: "center", width: "40vw"}}>
                                            <img style={{width: 250, height: 150, borderRadius: 5, marginLeft: 20}} src={val.imageUrl}/>
                                        </div>     */}
            </div>
          </div>
        );
      })}
      {/* <div className="button">
        <i class="fa fa-plus"></i>
      </div> */}
    </div>
  );
}

export default CustomDictionary;
