import React, { useState } from "react";
import "../../App.css";
import "./CustomDictionary.css";
import Cards from "../Cards";
import HeroSection from "../HeroSection";
import Footer from "../Footer";
var ProjectsJSON = [{ word: "John", image: "Doe" }];
function CustomDictionary() {
  const [inputTitle, setInputTitle] = useState("");
  const [inputDescription, setInputDescription] = useState("");
  const [inputTags, setInputTags] = useState("");

  function addWord() {
    ProjectsJSON.push({ word: "x", image: "Doe" });
    alert("pushed");
    setInputTitle("");
  }

  return (
    <div className="custom-dictionary">
      {ProjectsJSON.filter((val) => {
        if (inputTitle == "" && inputTags == "" && inputDescription == "") {
          return val;
        } else if (
          val.title.toLowerCase().includes(inputTitle.toLowerCase()) &&
          val.tags.toLowerCase().includes(inputTags.toLowerCase()) &&
          val.description.toLowerCase().includes(inputDescription.toLowerCase())
        ) {
          return val;
        }
      }).map((val, key) => {
        return (
          <div
            style={{
              borderRadius: 10,
              cursor: "pointer",
              backgroundColor: "white",
              margin: 20,
              padding: 20,
            }}
          >
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                justifyContent: "center",
              }}
            >
              <div style={{ width: 1000 }}>
                <strong>{val.word}</strong>
                <p>{val.image}</p>
              </div>
              <input type="text" maxLength="5" placeholder={val.word}></input>
              <input type="file" accept="image/png, image/jpeg"></input>
            </div>
          </div>
        );
      })}
      <p
        onClick={() => {
          addWord();
        }}
      >
        Add Word +
      </p>
      {/* <div style={{ backgroundColor: "blue" }}>
        <i class="fa fa-plus"></i>
      </div> */}
    </div>
  );
}

export default CustomDictionary;
